import React, {Component} from 'react';
import {
  ListItem,
  Left,
  Right,
  Thumbnail,
  Body,
  View,
  Text,
  Button,
} from 'native-base';
import {StyleSheet} from 'react-native';
import Time from './Time';

export default class NewsItem extends Component {
  constructor(props) {
    super(props);
    this.newsInfo = props.newsInfo;
  }
  
  handlePress = () => {
    const {url, title} = this.newsInfo
    this.props.onPress({url, title});
  };
  render() {
    return (
      <ListItem thumbnail>
        <Left>
          <Thumbnail
            square
            source={{
              uri:
                this.newsInfo.urlToImage != null
                  ? this.newsInfo.urlToImage
                  : 'data:image/png;base64,',
            }}
          />
        </Left>
        <Body>
          <Text numberOfLines={3}>{this.newsInfo.title}</Text>
          <Text note numberOfLines={2}>
            {this.newsInfo.description}
          </Text>
          <View style={styles.published}>
            <Text note style={styles.textStyles}>
              {this.newsInfo.source.name}
            </Text>
          </View>
          <View style={styles.published}>
            <Time published={this.newsInfo.publishedAt}></Time>
          </View>
        </Body>
        <Right>
          <Button transparent onPress={this.handlePress}>
            <Text>View</Text>
          </Button>
        </Right>
      </ListItem>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  textStyles: {
    color: '#808080',
    fontSize: 14,
  },
  published: {
    flex: 1,
    flexDirection: 'row',
    marginTop: 8,
    marginLeft: 0,
    color: 'red',
  },
});
